mapper.update:
update enterprise 
set ent_password = #{ent_password}, ent_contact = #{ent_contact}, ent_email = #{ent_email} 
where ent_index = #{ent_index}

mapper.select:
select * 
from enterprise 
where ent_name = #{ent_name} and ent_email = #{ent_email}

select * 
from enterprise 
where ent_id = #{ent_id} and ent_email = #{ent_email}
