mapper.select:
select ind_name
from individual
where  ind_id=#{ind_id} and ind_password=#{ind_password};

mapper.update:
update individual 
set ind_password = #{ind_password}, ind_phone = #{ind_phone}, ind_email = #{ind_email} 
where ind_index = #{ind_index}

mapper.select:
select * 
from individual 
where ind_name = #{name} and ind_phone = #{phone}

select * 
from individual 
where ind_id = #{id} and ind_phone = #{phone}
